import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import routes from '../../routes'
import "./title.css"; 

const Title = (route) => {
    const r = route.route;

    const subMenu = (children) => children.map((sub) => {
        console.log(r);
        if(sub){
            return (
            <NavLink to={`${r.route}/${sub.route}`} className="sub-nav-item">
                <div className="nav-progress-text">
                                    <img src={`/images/${route.icon}`} alt="Route icon" className={isSidebarVisible ? "nav-image" : "nav-image-hidden"} />
                                    <span className="nav-linkname" hidden={!isSidebarVisible}>{route.name}</span>
                                    <span className="nav_completation" hidden={!isSidebarVisible}>{route.completed_sets}/{route.total_sets}</span>
                                </div>
            </NavLink>);
        }
    });


  return(
    <div className="title-container">
        <div className="title-left">
            <img src={`/images/${r.icon}`} alt="Route icon" className="title-image" />
        </div>
        <div className="title-content">
            <div className="title-right">
                <h1 className="title-name">{r.name}</h1>
            </div>
            <div className="sub-navigation">
                <nav className="sub-nav-menu">
                {subMenu(r.children)}
                </nav>
            </div>
        </div>
    </div>
  );
}

export default Title;
