import React, { useState, useEffect } from 'react';
import "./addactivities.css"; 
import WordPairs from './activitySetups/wordPairs';

const AddActivites = () => {
    const [currentState, setCurrentState] = useState(0);
    const [outJSON, setoutJSON] = useState({});


    const questions = [
        { id: 1, question: 'Section', type:'buttons'},
        { id: 2, question: 'Activity Name', type:'text'},
        { id: 3, question: 'Activity Setup', type:''}
    ];

    const activityType = [
        [
            {name: "Write", type:"listSound"},
            {name: "Match", type:"listSound"},
            {name: "Order", type:"list"}], //Alphabet
        [
            {name: "Convert", type:"wordPairs"},
            {name: "Match", type:"wordPairs"},
            {name: "Fill in the Blanks", type: "textWords"}], //Vocabulary
        [
            {name: "Convert", type:"sentPairs"},
            {name: "Correct", type:"sentCorrect"},
            {name: "Structure", type:"sentElements"}], //Sentences
        [
            {name: "Gender", type: "tenseGender"},
            {name: "Plural", type:"wordPairs"},
            {name: "Word NGA", type: "wordNGA"},
            {name: "Sentence NGA", type: "sentNGA"}], //Tenses
        [
            {name: "Topic", type:"text"},
            {name: "Word Practice", type:"list"}], //Writing
        [
            {name: "Text", type:"none"}] //Reading
    ];
    
    const handleSectionInput = (id) => {
        setoutJSON({
            ...outJSON,
            Section: id,
        });
    };

    const handleNameChange = (e) => {
        const { value } = e.target;
        setAnswers({
            ...answers,
            [questions[currentState].id]: value,
        });
    };
    
    const handleWordPairChange = (index, field, value) => {
        const newWordPairs = [...wordPairs];
        newWordPairs[index][field] = value;
        setWordPairs(newWordPairs);
    };
    
    const addWordPair = () => {
        setWordPairs([...wordPairs, { english: '', greek: '' }]);
    };
    
    const goToNext = () => {
        if (currentState < questions.length - 1) {
            setCurrentState(currentState + 1);
        }
    };

    const goToPrevious = () => {
        if (currentState > 0) {
            setCurrentState(currentState - 1);
        }
    };

    console.log(answers);
    return (
        <div className="add-container">
          <h2>{questions[currentState].question}</h2>
          <form>
            {currentState == 0 ? ( //Initial buttons to select section
              <div className="add-btn-container">
                <button type="button" onClick={() => handleSectionInput(0)} className={answers[1] == 0 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Alphabet</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(1)} className={answers[1] == 1 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/wordIcon.png`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Vocabulary</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(2)} className={answers[1] == 2 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/sentenceIcon.png`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Sentences</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(3)} className={answers[1] == 3 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/tensesIcon.png`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Tenses</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(4)} className={answers[1] == 4 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/writingIcon.png`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Writing</label>                </button>
                <button type="button" onClick={() => handleSectionInput(5)} className={answers[1] == 5 ? "add-btn-section-active" : "add-btn-section" }>
                    <img src={`/images/readingIcon.png`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Reading</label>
                </button>
              </div>
            ) : currentState == 1 ? ( //Enter activity name
                <div className="add-name-container">
                    <input
                    className="add-name-input"
                    type={questions[currentState].type}
                    value={answers[questions[currentState].id] || ''}
                    onChange={handleNameChange}
                    autoFocus
                    />  
                </div>
            ) : ( //Data Input
              <div>
                {showWordPairs ? (
                  <WordPairs
                    wordPairs={wordPairs}
                    onWordPairChange={handleWordPairChange}
                    onAddWordPair={addWordPair}
                  />
                ) : (
                  <div>Other Settings Component</div>
                )}
              </div>
            )}

            <div className="add-btns-form">
                {currentState > 0 && (
                    <button type="button" onClick={goToPrevious} className="add-btn-previous">
                    Previous
                    </button>
                )}
                
                {currentState < questions.length - 1 ? (
                    <button type="button" onClick={goToNext} className="add-btn-next"> 
                    Next
                    </button>
                ) : (
                    <button type="button" onClick={() => console.log('Form submitted:', answers, wordPairs)} className="add-btn-submit">
                    Submit
                    </button>
                )}
            </div>
          </form>
        </div>
    );
};

export default AddActivites;
