import React, { useState, useEffect } from 'react';

const WordPairs = ({ outChange, data = []} ) => {
    const [items, setItems] = useState(data);

    const addWordPair = () => {
        const newItems = [...items, [""]];
        setItems(newItems);
        outChange(newItems);
    };

    const handleWordPairChange = (index, field, value) => {
        const newItems = [...items];
        newItems[index][field] = value;
        setItems(newItems);
        outChange(newItems);
    };

    return (
        <div className="setup-container">
            <label>Define all items in order the user must enter them</label>
            <button type="button" className="pairs-btn" onClick={addWordPair}>
                Add Item
            </button>
            <br></br>
            {items.map((pair, index) => (
            <div key={index} className="pairs-input-container">
                <label>{index+1}: </label>
                <input
                type="text"
                value={pair}
                onChange={(e) => handleWordPairChange(index, 'A', e.target.value)}
                className="pairs-input"
                />
            </div>
            ))}
        </div>
    );
};

export default WordPairs;
