import { useState, useEffect } from "react";
import './App.css';
import { BrowserRouter, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Home from './components/home/home'
import Login from './components/Login/login';
import Header from './components/header/header';
import routes from './routes'

const ProtectedRoute = ({ children }) => {
  const token = sessionStorage.getItem('token');
  return token ? children : <Navigate to="/" />;
};

const getRoutes = (allRoutes) => allRoutes.map((route) => {
  if(route.collapse){
    return getRoutes(route.collapse);
  }

  if(route.route){
    return <Route path={route.route} element={<ProtectedRoute> route.component </ProtectedRoute>} key={route.key} />;
  }

  return null;
});

function App() {
  const [token, setToken] = useState(sessionStorage.getItem('token') || '');
  const [userid, setUserid] = useState(sessionStorage.getItem('userid') || 0);

  return (
    <div className="wrapper">
      {userid !== 0 && <Header />}
      <BrowserRouter>
        <Routes>
            <Route path="/"  element={ <Login />} />
            {getRoutes(routes)}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
