import React, { useState } from 'react';

const WordPairs = ({ outChange, options = {A: "English", B: "Greek"} }) => {
    const [wordPairs, setWordPairs] = useState([{ A: '', greek: '' }]);

    const addWordPair = () => {
        setWordPairs([...wordPairs, { english: '', greek: '' }]);
    };

    const handleWordPairChange = (index, field, value) => {
        const newWordPairs = [...wordPairs];
        newWordPairs[index][field] = value;
        setWordPairs(newWordPairs);
        outChange(wordPairs);
    };

    return (
        <div>
            <label>Add English and Greek word pairs:</label>
            {wordPairs.map((pair, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
                <input
                type="text"
                placeholder="English Word"
                value={pair.english}
                onChange={(e) => handleWordPairChange(index, 'english', e.target.value)}
                style={{ marginRight: '10px' }}
                />
                <input
                type="text"
                placeholder="Greek Word"
                value={pair.greek}
                onChange={(e) => handleWordPairChange(index, 'greek', e.target.value)}
                />
            </div>
            ))}
            <button type="button" onClick={addWordPair}>
            Add Word Pair
            </button>
        </div>
    );
};

export default WordPairs;
