import React, { useState, useEffect } from 'react';
import "./addactivities.css"; 
import WordPairs from './activitySetups/wordPairs';

const AddActivites = () => {
    const [currentState, setCurrentState] = useState(0);
    const [answers, setAnswers] = useState({});
    const [wordPairs, setWordPairs] = useState([{ english: '', greek: '' }]);
    const [showWordPairs, setShowWordPairs] = useState(true); // Toggle between settings components


    const questions = [
        { id: 1, question: 'Section', type:'buttons'},
        { id: 2, question: 'Name', type:'text'},
        { id: 3, question: 'Setup', type:''}
    ];
    
    const handleSectionInput = (id) => {
        setAnswers({
            ...answers,
            [questions[currentState].id]: id,
        });
    };

    const handleInputChange = (e) => {
        const { value } = e.target;
        setAnswers({
            ...answers,
            [questions[currentState].id]: value,
        });
    };
    
    const handleWordPairChange = (index, field, value) => {
        const newWordPairs = [...wordPairs];
        newWordPairs[index][field] = value;
        setWordPairs(newWordPairs);
    };
    
    const addWordPair = () => {
        setWordPairs([...wordPairs, { english: '', greek: '' }]);
    };
    
    const goToNext = () => {
        if (currentState < questions.length - 1) {
            setCurrentState(currentState + 1);
        }
    };

    const goToPrevious = () => {
        if (currentState > 0) {
            setCurrentState(currentState - 1);
        }
    };

    return (
        <div>
          <h2>Adding Activity</h2>
          <form>
            {currentState == 0 ? ( //Initial buttons to select section
              <>
                <button type="button" onClick={() => handleSectionInput(0)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Alphabet</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(1)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Vocabulary</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(2)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Sentences</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(3)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Tenses</label>
                </button>
                <button type="button" onClick={() => handleSectionInput(4)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Alphabet</label>                </button>
                <button type="button" onClick={() => handleSectionInput(5)}>
                    <img src={`/images/alphIcon.PNG`} alt="Route icon" className="add-btn-section-button-img"/>
                    <label>Alphabet</label>
                </button>
              </>
            ) : currentState == 1 ? ( //Enter activity name
                <div>
                    <label>{questions[currentState].question}</label>
                    <input
                    type={questions[currentState].type}
                    value={answers[questions[currentState].id] || ''}
                    onChange={handleInputChange}
                    autoFocus
                    />  
                </div>
            ) : ( //Data Input
              <div>
                <label>{questions[currentState].question}</label>
                {showWordPairs ? (
                  <WordPairs
                    wordPairs={wordPairs}
                    onWordPairChange={handleWordPairChange}
                    onAddWordPair={addWordPair}
                  />
                ) : (
                  <div>Other Settings Component</div>
                )}
              </div>
            )}

            <div style={{ marginTop: '20px' }}>
                {currentState > 0 && (
                    <button type="button" onClick={goToPrevious} style={{ marginRight: '10px' }}>
                    Previous
                    </button>
                )}
                
                {currentState < questions.length - 1 ? (
                    <button type="button" onClick={goToNext}>
                    Next
                    </button>
                ) : (
                    <button type="button" onClick={() => console.log('Form submitted:', answers, wordPairs)}>
                    Submit
                    </button>
                )}
            </div>
          </form>
        </div>
    );
};

export default AddActivites;
