            route.completed_sets / route.total_sets;
        return (
            <li>
                <div className="nav-progress">
                    <NavLink to={route.route}>
                        <div className="nav-progress-bar" style={{ width: `20%` }}></div>
                        <div className="nav-progress-text">
                            {route.name} {20}%
                        </div>
                    </NavLink>
                </div>
            </li>);
    }

    return null;
});

function Navigation() {


  return(
    <div className="navbar">
        <nav>
            <ul>
                {getRoutes(routes)}
            </ul>
        </nav>
    </div>
  );
}

export default Navigation;


