import { useState, useEffect } from "react";
import './App.css';
import { BrowserRouter, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Home from './components/home/home'
import Login from './components/Login/login';
import Header from './components/header/header';
import routes from './routes'
import PageWrapper from './components/pagewrapper'

const getRoutes = (allRoutes) => allRoutes.map((route) => {
  if(route.collapse){
    return getRoutes(route.collapse);
  }

  if(route.route){
    return <Route path={route.route} element={ route.component} key={route.key} />;
  }

  return null;
});

function App() {
  const [token, setToken] = useState(sessionStorage.getItem('token') || '');
  const [userid, setUserid] = useState(sessionStorage.getItem('userid') || 0);
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogin = (newToken, newId) => {
    setToken(newToken);
    setUserid(newId);
    sessionStorage.setItem('token', newToken);
    sessionStorage.setItem('userid', newId);
    navigate('/home');
  };

  const toggleSidebar = () => {
    setIsSidebarVisible(!isSidebarVisible);
  };

  useEffect(() => {
    if (!token && location.pathname !== '/') {
        navigate('/');
    }
  }, [token, navigate, location.pathname]);

  return (
    <div className="wrapper">
      <PageWrapper className={!token ? "login-background" : "home-background"}>
        {token && <Header userId={userid} />}
        <div className={`right-panel ${token && "dashboard"}`}>
          <Routes>
              <Route path="/"  element={ <Login onLogin={handleLogin} />} />
              {getRoutes(routes)}
          </Routes>
        </div>  
      </PageWrapper>
    </div>
  );
}

const WrappedApp = () => (
  <BrowserRouter>
      <App />
  </BrowserRouter>
);

export default WrappedApp;
