import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import routes from '../../routes'
import "./title.css"; 

const Title = (route) => {
    const r = route.route;
    const location = useLocation(); // Get the current path
    const currentPath = location.pathname;

    const subMenu = (children) => children.map((sub) => {
        console.log(r);
        if(sub){
            return (
            <NavLink to={`${r.route}/${sub.route}`} className={currentPath !== route.route ? "nav-progress" : "nav-progress-active" }>
                <img src={`/images/${route.icon}`} className="sub-nav-image"/>
                <span className="sub-nav-linkname" >{sub.name}</span>
            </NavLink>);
        }
    });


  return(
    <div className="title-container">
        <div className="title-left">
            <img src={`/images/${r.icon}`} alt="Route icon" className="title-image" />
        </div>
        <div className="title-content">
            <div className="title-right">
                <h1 className="title-name">{r.name}</h1>
            </div>
            <div className="sub-navigation">
                <nav className="sub-nav-menu">
                {subMenu(r.children)}
                </nav>
            </div>
        </div>
    </div>
  );
}

export default Title;
