import React, { useState, useEffect } from 'react';

const TextWithWordList = ({outChange, initialText = '', initialWords = [] }) => {
  const [longText, setLongText] = useState(initialText);
  const [words, setWords] = useState(initialWords.length > 0 ? initialWords : []);

  // Handle text input change
  const handleTextChange = (e) => {
    setLongText(e.target.value);
    var out = [
        {text: e.target.value},
        {words: words}
    ];

    outChange(out);
  };

  // Handle individual word change
  const handleWordChange = (index, value) => {
    const newWords = [...words];
    newWords[index] = value;
    setWords(newWords);

    var out = [
        {text: longText},
        {words: newWords}
    ];
    outChange(out);
  };

  // Add a new word input
  const addWord = () => {
    setWords([...words, '']);
  };

  // Remove a word input
  const removeWord = (index) => {
    setWords(words.filter((_, i) => i !== index));
  };

  return (
    <div className="setup-container">
      <h3>Enter text here with %(#) to indicate a blank where # is the word that should be there</h3>
      <textarea
        value={longText}
        onChange={handleTextChange}
        placeholder= {longText}
        rows="10"
        className="test-longtext"
        style={{ width: '100%', marginBottom: '20px' }}
      />

      <h3>Word List</h3>
      {words.map((word, index) => (
        <div key={index} claaName="text-add" style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <label>{index}:</label>
          <input
            type="text"
            value={word}
            onChange={(e) => handleWordChange(index, e.target.value)}
            placeholder={`Word ${index + 1}`}
            style={{ marginRight: '10px' }}
          />
          <button type="button" onClick={() => removeWord(index)}>
            Remove
          </button>
        </div>
      ))}
      
      <button type="button" onClick={addWord} style={{ marginTop: '10px' }}>
        Add Word
      </button>
    </div>
  );
};

export default TextWithWordList;
