import React, {useEffect, useState} from 'react';
import { NavLink } from "react-router-dom";
import routes from '../../routes'
import './navigation.css';

const getRoutes = (allRoutes) => allRoutes.map((route) => {
    if(route.collapse){
        return getRoutes(route.collapse);
    }

    if(route.route){
        var progress = 0;
        if(route.total_sets != 0)
            progress = (route.completed_sets / route.total_sets) * 100;
        if(route.sets){
            return (
                <li className={!route.bottom ? "nav-item" : "nav-item-bottom"}>
                    <div className="nav-progress">
                        <NavLink to={route.route}>
                            <div className="nav-progress-bar" style={{ width: `${progress}%` }}></div>
                            <div className="nav-progress-text">
                                <img src={`/images/${route.icon}`} alt="Route icon" className="nav-image" />
                                <span className="nav-linkname">{route.name}</span>
                                <span className="nav_completation">{route.completed_sets}/{route.total_sets}</span>
                            </div>
                        </NavLink>
                    </div>
                </li>);
        }
        else {
            return (
                <li className={!route.bottom ? "nav-item" : "nav-item-bottom"}>
                    <div className="nav-progress">
                        <NavLink to={route.route}>
                            <div className="nav-progress-text">
                                <img src={`/images/${route.icon}`} alt="Route icon" className="nav-image" />
                                <span className="nav-linkname">{route.name}</span>
                            </div>
                        </NavLink>
                    </div>
                </li>
            );
        }
    }

    return null;
});

const Navigation = () => {
    const [isSidebarVisible, setIsSidebarVisible] = useState(true);
  return(
    <div className="navbar">
        <nav>
            <ul>
                {getRoutes(routes)}
            </ul>
        </nav>
    </div>
  );
}

export default Navigation;
