import React from 'react';

const WordPairs = ({ wordPairs, onWordPairChange, onAddWordPair }) => {
  return (
    <div>
      <label>Add English and Greek word pairs:</label>
      {wordPairs.map((pair, index) => (
        <div key={index} style={{ marginBottom: '10px' }}>
          <input
            type="text"
            placeholder="English Word"
            value={pair.english}
            onChange={(e) => onWordPairChange(index, 'english', e.target.value)}
            style={{ marginRight: '10px' }}
          />
          <input
            type="text"
            placeholder="Greek Word"
            value={pair.greek}
            onChange={(e) => onWordPairChange(index, 'greek', e.target.value)}
          />
        </div>
      ))}
      <button type="button" onClick={onAddWordPair}>
        Add Word Pair
      </button>
    </div>
  );
};

export default WordPairs;
