import { useState, useEffect } from "react";
import './App.css';
import { BrowserRouter, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Home from './components/home/home'
import Login from './components/Login/login';
import Header from './components/header/header';
import routes from './routes'

const getRoutes = (allRoutes) => allRoutes.map((route) => {
  if(route.collapse){
    return getRoutes(route.collapse);
  }

  if(route.route){
    return <Route path={route.route} element={ route.component} key={route.key} />;
  }

  return null;
});

function App() {
  const [token, setToken] = useState(sessionStorage.getItem('token') || '');
  const [userid, setUserid] = useState(sessionStorage.getItem('userid') || 0);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Check if a token exists in session storage
    const token = sessionStorage.getItem('token');

    // Redirect to the login page if there is no token and not already on the login page
    if (!token && location.pathname !== '/') {
        navigate('/');
    }
  }, [navigate, location.pathname]);

  return (
    <div className="wrapper">
      {userid !== 0 && <Header />}
      <div className="right-panel {}">
        <Routes>
            <Route path="/"  element={ <Login />} />
            {getRoutes(routes)}
        </Routes>
      </div>
    </div>
  );
}

const WrappedApp = () => (
  <BrowserRouter>
      <App />
  </BrowserRouter>
);

export default WrappedApp;
